import styled from "styled-components";


const Sec02Top = styled.div`
  & > h2 {
    font-size: 45px;
    font-weight: 45px;
    font-family: 'KOTRA HOPE';
    font-weight: normal;
    font-style: normal;
    background: linear-gradient(#39DC72 60%, #27E4A8 100%);
    background-clip: text;
    -webkit-background-clip: text;
    color: transparent;
    margin-bottom: 20px;
  }
  & > p {
    font-size: 18px;
    font-weight: 20px;
    color: #3DAD72;
    margin-bottom: 50px;
  }
`;
const Sec02ContainerDiv = styled.div`
  width: 100%;
  position: relative;
`;
const Sec02Img = styled.div`
  width: 100%;
  border-radius: 20px;
`;
const Sec02Text = styled.div`
  z-index: 1;
  display: flex;
  position: absolute;
  top:0;
  border-radius: 15px;
  width: 100%;
  height: 100%;
  background-color: #3DAD72;
  text-align: center;
  color: #fff;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  opacity: 0;
  transition: 0.6s;
  & > h2{
    font-size: 26px;
    line-height: 28px;
    font-weight: 600;
  }
  &:hover{
     opacity: 0.9;
  }
`

function Section02() {

  return (
    <ContainerDiv className="padding-common-120">
      <div className="row">
        <Sec02Top className="col-md-12 text-center">
          <h2>추천글랭핑</h2>
          <p>여행 일정을 선택하고 특가를 확인하세요!</p>
        </Sec02Top>
      </div>
      <div className="row">
        <div className="col-lg-3 col-sm-6 gy-4">
          <Sec02ContainerDiv>
            <Sec02Img>
              <img src="assets/imges/glam1.png" alt="glam1"/>
            </Sec02Img>
            <Sec02Text>
              <h2>강화 아로니움</h2>
              <p>캠핑 · 글램핑</p>
            </Sec02Text>
          </Sec02ContainerDiv>
        </div>
      </div>
    </ContainerDiv>
  );
};
export default Section02;
